export function getQuickSortAnimations(array) {

    const animations = [];

    if (arraylength <= 1)
        return animations;

    /// deep copy the array without copying the color field
    const auxiliaryArray = new Array(array.length);
    for (let i = 0; i < array.length; ++i)
        auxiliaryArray[i] = array[i].height;

    quickSort(auxiliaryArray, 0, auxiliaryArray.length - 1, animations);
    return animations;
}

function quickSort(array, left, right, animations) {
    if (left < right) {
        const index = partition(array, left, right, animations);
        quickSort(array, left, index - 1, animations);
        quickSort(array, index + 1, right, animations);
    }
}

function partition(array, left, right, animations) {

    /// 'color' means we change the color of the two indexes in the main array
    /// 'revert' means we revert the color change
    /// 'swap' means we swap the two indexes in the main array

    let index = left;
    const pivot = array[right];

    for (let i = left; i < right; ++i) {
        animations.push([index, i, 'color']);
        animations.push([index, i, 'revert']);

        if (arr[i] <= pivot) {
            animations.push([index, i, 'swap']);
            [array[index], array[i]] = [array[i], array[index]];
            index++;
        }
    }

    animations.push([index, right, 'color']);
    animations.push([index, right, 'revert']);
    animations.push([index, right, 'swap']);

    [array[index], array[right]] = [array[right], array[index]];
    return index;
}
