import React from 'react';
import { getBubbleSortAnimations, getHeapSortAnimations, getMergeSortAnimations, getQuickSortAnimations } from '../sortingAlgorithms/SortingAlgorithms.js';

import './SortingVisualizer.css';

// Value for the speed of the animations.
const ANIMATION_SPEED_MS = 2;

// Value for the number of bars (value) in the array.
const NUMBER_OF_ARRAY_BARS = 310;

// This is the main color of the array bars.
const PRIMARY_COLOR = 'turquoise';

// This is the color of array bars that are being compared throughout the animations.
const SECONDARY_COLOR = 'red';

export default class SortingVisualizer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      array: [],
    };
  }

  componentDidMount() {
    this.resetArray();
  }

  resetArray() {
    const array = [];
    for (let i = 0; i < NUMBER_OF_ARRAY_BARS; i++) {
      array.push(randomIntFromInterval(5, 730)); //if we use 1 instead of 5, we will barely see it on the screen, Also duplicate values are allowed
    }
    this.setState({ array });
  }

  mergeSort() {
    const animations = getMergeSortAnimations(this.state.array);
    for (let i = 0; i < animations.length; i++) {
      const arrayBars = document.getElementsByClassName('array-bar');
      const isColorChange = i % 3 !== 2;
      if (isColorChange) {
        const [barOneIdx, barTwoIdx] = animations[i];
        const barOneStyle = arrayBars[barOneIdx].style;
        const barTwoStyle = arrayBars[barTwoIdx].style;
        const color = i % 3 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
        setTimeout(() => {
          barOneStyle.backgroundColor = color;
          barTwoStyle.backgroundColor = color;
        }, i * ANIMATION_SPEED_MS);
      } else {
        setTimeout(() => {
          const [barOneIdx, newHeight] = animations[i];
          const barOneStyle = arrayBars[barOneIdx].style;
          barOneStyle.height = `${newHeight}px`;
        }, i * ANIMATION_SPEED_MS);
      }
    }
  }


  bubbleSort() {
    const arrayBars = document.getElementsByClassName('array-bar');
    for (let i = 0; i < arrayBars.length; i++) {
      let j
      setTimeout(() => {
        for (j = 0; j < arrayBars.length - 1 - i; j++) {
          if (arrayBars[j + 1].clientHeight < arrayBars[j].clientHeight) {
            let tmp = arrayBars[j + 1].clientHeight;
            arrayBars[j + 1].style.height = `${arrayBars[j].clientHeight}px`;
            arrayBars[j].style.height = `${tmp}px`;


          }
        }
        arrayBars[j].style.backgroundColor = SECONDARY_COLOR;


      }, i * ANIMATION_SPEED_MS);


    }
  }

  insertionSort() {
    const arrayBars = document.getElementsByClassName('array-bar');
    for (let i = 1; i < arrayBars.length; i++) {
      let v = arrayBars[i].clientHeight;
      let j = i - 1;
      setTimeout(() => {
        while (j >= 0 && arrayBars[j].clientHeight > v) {
          arrayBars[j + 1].style.height = `${arrayBars[j].clientHeight}px`;
          arrayBars[j + 1].style.backgroundColor = SECONDARY_COLOR;
          j = j - 1;

        }
        arrayBars[j + 1].style.height = `${v}px`
        // arrayBars[j+1].style.backgroundColor = SECONDARY_COLOR;
      }, i * ANIMATION_SPEED_MS);

    }
  }
  quickSort() {
  }

  heapSort() {
  }


  render() {
    const { array } = this.state;

    return (
      <div className="array-container">
        {array.map((value, idx) => (
          <div
            className="array-bar"
            key={idx}
            style={{
              backgroundColor: PRIMARY_COLOR,
              height: `${value}px`,
            }}></div>
        ))}
        <button onClick={() => this.resetArray()}>Generate New Array</button>
        <button onClick={() => this.SelectionSort()}>Selection Sort</button>
        <button onClick={() => this.mergeSort()}>Merge Sort</button>
        <button onClick={() => this.quickSort()}>Quick Sort</button>
        <button onClick={() => this.heapSort()}>Heap Sort</button>
        <button onClick={() => this.bubbleSort()}>Bubble Sort</button>
      </div>
    );
  }
}

// From https://stackoverflow.com/questions/4959975/generate-random-number-between-two-numbers-in-javascript
function randomIntFromInterval(min, max) {
  // min and max included
  return Math.floor(Math.random() * (max - min + 1) + min);
}

